opam.opam:
opam-version: "1.2"
maintainer: "whitequark <whitequark@whitequark.org>"
authors: "whitequark <whitequark@whitequark.org>"
license: "MIT"
tags: ["syntax" "json"]
homepage: "https://github.com/whitequark/ppx_deriving_yojson"
doc: "http://whitequark.github.io/ppx_deriving_yojson"
bug-reports: "https://github.com/whitequark/ppx_deriving_yojson/issues"
depends: [
  "yojson"
  "result"
  "ppx_deriving" {>= "4.0" & < "5.0"}
  "ocamlfind" {build}
  "ocamlbuild" {build}
  "cppo" {build}
  "cppo_ocamlbuild" {build}
  "ounit" {test}
  "ppx_import" {test & >= "1.1"}
]
conflicts: [
  "ppx_deriving" {= "4.2"}
]
build: [
  "ocaml"
  "pkg/build.ml"
  "native=%{ocaml-native}%"
  "native-dynlink=%{ocaml-native-dynlink}%"
]
run-test: [
  "ocamlbuild"
  "-classic-display"
  "-use-ocamlfind"
  "src_test/test_ppx_yojson.byte"
  "--"
]
substs: "pkg/META"
dev-repo: "git://github.com/whitequark/ppx_deriving_yojson.git"

opam-version: "1.2"
name: "js_of_ocaml-compiler"
maintainer: "dev@ocsigen.org"
authors: "Ocsigen team"
homepage: "http://ocsigen.org/js_of_ocaml"
bug-reports: "https://github.com/ocsigen/js_of_ocaml/issues"
depends: [
  "jbuilder" {build & >= "1.0+beta17"}
  "cmdliner"
  "cppo" {>= "1.1.0"}
  "ocamlfind"
  "yojson"
]
conflicts: [
  "ocamlfind" {< "1.5.1"}
  "js_of_ocaml" {< "3.0"}
]
available: ocaml-version >= "4.02.0"
build: ["jbuilder" "build" "-p" name "-j" jobs]
dev-repo: "git+https://github.com/ocsigen/js_of_ocaml.git"

opam-version: "1.2"
maintainer: "martin@mjambon.com"
authors: "Martin Jambon"
license: "BSD-3-Clause"
homepage: "http://mjambon.com/cppo.html"
bug-reports: "https://github.com/mjambon/cppo/issues"
depends: [
  "jbuilder" {build & >= "1.0+beta10"}
  "ocamlbuild"
  "cppo" {>= "1.6.0"}
]
build: [
  ["jbuilder" "subst" "-p" name] {pinned}
  ["jbuilder" "build" "-p" name "-j" jobs]
]
dev-repo: "git+https://github.com/mjambon/cppo.git"

opam-version: "1.2"
maintainer: "unixjunkie@sdf.org"
authors: "Carlo Wood"
license: "GPL-2+"
homepage: "http://www.gnu.org/software/which/"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["which" "which"]
depexts: [
  ["which"] {"centos"}
  ["which"] {"fedora"}
  ["which"] {"opensuse"}
  ["debianutils"] {"debian"}
  ["debianutils"] {"ubuntu"}
  ["which"] {"nixpkgs"}
  ["which"] {"archlinux"}
]

opam-version: "1.2"
maintainer: "tim@gfxmonk.net"
license: "GPL-3"
homepage: "http://www.gnu.org/software/m4/m4.html"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["sh" "-exc" "echo | m4"]
depexts: [
  ["m4"] {"debian"}
  ["m4"] {"ubuntu"}
  ["m4"] {"fedora"}
  ["m4"] {"rhel"}
  ["m4"] {"centos"}
  ["m4"] {"alpine"}
  ["m4"] {"nixpkgs"}
  ["m4"] {"opensuse"}
  ["m4"] {"oraclelinux"}
  ["m4"] {"archlinux"}
]
